trees :: [[Char]] -> Int -> Int -> Int
trees text across down = do 
  let end = length text
  let width = length (head text)

  let path = [(text!!(n*down))!!((n*across) `mod` width)| n <- [0..(div end down)], n*down<end]

  (length [n|n <- path, n=='#'])

  
